// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.18 <0.9.0;

contract MarcCoin {
event Transfer(address indexed from, address indexed to, uint tokens);
event Approval(address indexed tokenOwner, address indexed spender, uint tokens);
event WinningMessage(string message);
string public constant name = "Marc Coin";
string public constant symbol = "MARC";
uint8 public constant decimals = 18;

mapping(address => uint256) balances;
mapping(address => uint256) pot;
mapping(address => mapping (address => uint256)) allowed;

uint256 totalSupply_;
address lotteryAddress; // where the money will be going (ie fundraiser)
address smartContractAddress;
uint256 lotteryPot;
string youWinResponse;
string youLoseResponse;
uint winningNumber;
constructor(uint256 total, address lotteryOwnerAddress ) {
  totalSupply_ = total;
  balances[msg.sender] = totalSupply_;
  lotteryPot = 0;
  smartContractAddress = msg.sender;

  lotteryAddress = lotteryOwnerAddress;
  youWinResponse = "You've won the lottery";
  youLoseResponse = "You've lost the lottery, try again!";
}

function resetLotteryMessage() public {
    emit WinningMessage("Try your luck at the lottery!");
}

function getLotteryAddress() public view returns (address){
    return lotteryAddress;
}

function balanceOf(address tokenOwner) public view returns (uint) {
    return balances[tokenOwner];
}

function getLotteryPot() public view returns (uint){
    return lotteryPot;
}

function transfer(address receiver, uint numTokens) public returns (bool) {
    require(numTokens <= balances[msg.sender]);
    balances[msg.sender] -= numTokens;
    balances[receiver] += numTokens;
    emit Transfer(msg.sender, receiver, numTokens);
    return true;
}

function transferFrom(address owner, address buyer, uint numTokens) public returns (bool) {
    require(numTokens <= balances[owner]);
    // require(numTokens <= allowed[owner][msg.sender]);
    balances[owner] -= numTokens;
    // allowed[owner][msg.sender] -= numTokens;
    balances[buyer] += numTokens;
     emit Transfer(owner, buyer, numTokens);
    return true;
} 

function playLottery(address lotteryPlayer, uint numberChoice) public returns (string memory){
// take a Marc Coin
transfer(smartContractAddress, 1);
 lotteryPot = lotteryPot + 1;

     // random number
        winningNumber = generateRandomNumber(totalSupply_);
        if(winningNumber == numberChoice){
        
        transferFrom(smartContractAddress, lotteryPlayer, lotteryPot /10 * 8 + balances[smartContractAddress]/10 * 8 );
        transferFrom(smartContractAddress, lotteryAddress, lotteryPot /10 * 2 + balances[smartContractAddress]/10 * 2);
        lotteryPot = 0;
        emit WinningMessage(youWinResponse);
        return youWinResponse;
        } else{
        emit WinningMessage(youLoseResponse);
        return youLoseResponse;
        }
}

function generateRandomNumber(uint number) private view returns(uint){
       return uint(keccak256(abi.encodePacked(block.timestamp,block.prevrandao,  
        msg.sender))) % number;
}
// function approve(address delegate, uint numTokens) public returns (bool) {
//     allowed[msg.sender][delegate] = numTokens;
//     emit Approval(msg.sender, delegate, numTokens);
//     return true;
// }
}
